class News {
  String _status;
  int _totalResults;
  List<Articles> _articles;

  String get status => _status;

  int get totalResults => _totalResults;

  List<Articles> get articles => _articles;

  News({String status, int totalResults, List<Articles> articles}) {
    _status = status;
    _totalResults = totalResults;
    _articles = articles;
  }

  News.fromJson(dynamic json) {
    _status = json["status"];
    _totalResults = json["totalResults"];
    if (json["articles"] != null) {
      _articles = [];
      json["articles"].forEach((v) {
        _articles.add(Articles.fromJson(v));
      });
    }
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["status"] = _status;
    map["totalResults"] = _totalResults;
    if (_articles != null) {
      map["articles"] = _articles.map((v) => v.toJson()).toList();
    }
    return map;
  }

  @override
  String toString() {
    return 'News{_status: $_status, _totalResults: $_totalResults, _articles: $_articles}';
  }
}

